import React, { useState } from "react";
import './Header.css';
import { Link, useLocation } from 'react-router-dom';

export function Hero() {
    return (
        <div className="hero">
            <div className="hero-paragraphs">
                <p>Social dance is for everyone</p>
            </div>
        </div>
    )
}

export default function Header() {
    const [menuOpen, setMenuOpen] = useState(false); // State to track menu open/close
    const location = useLocation();

    const toggleMenu = () => {
        setMenuOpen(!menuOpen); // Toggle the menu state
    };

    return (
        <section className="header-background" style={{
            backgroundImage: `url(${process.env.PUBLIC_URL}/images/square-background.png)`
        }}>
            <section className="header">
                <nav className="header-nav">
                    {/* <img className="header-logo" src="/images/Center_Centre_Logo.png" alt="center centre logo" /> */}

                    {/* Hamburger icon */}
                    <div className="hamburger" onClick={toggleMenu}>
                        <span className="bar"></span>
                        <span className="bar"></span>
                        <span className="bar"></span>
                    </div>

                    {/* Links */}
                    <div className={`header-links ${menuOpen ? 'open' : ''}`}>
                        <Link to="/">Home</Link>
                        <Link to="/page1">Page1 (events)</Link>
                        <Link to="/topics">Page2 (Mission)</Link>
                        <Link to="/teams">Page3 (Programs)</Link>
                        <Link to="/october">Page4 (Teams)</Link>
                        <Link to="/contactUs">Page5 (contactUs)</Link>
                    </div>
                </nav>
                <div>
                    <h1>HUSTLE ∑¥ CLUB</h1>
                    <p className="online-course">OUR NEXT WEDNESDAY SOCIAL IS 11/2... <Link to="/Events">see details</Link></p>
                    <Hero />
                </div>
                {/* {location.pathname === '/' && <Hero />} */}
            </section>
        </section>
    );
}
